import Vue from "vue";
import { post } from "../../tools/ajax";
import Datepicker from "vuejs-datepicker";
export default {
  data: function data() {
    return {
      kuaixuns: [],
      startDate: "",
      endDate: "",
      keyword: "",
      bull: 0,
      bear: 0
    };
  },
  components: {
    Datepicker: Datepicker
  },
  methods: {
    getData: function getData() {
      var _this = this;

      var config = {
        startDate: this.startDate,
        endDate: this.endDate,
        keyword: this.keyword || ""
      };
      post("kuaixunData", config, function (err, res) {
        if (err || res.error) return console.error(err, res.error);
        _this.kuaixuns = res;

        for (var i = 0; i < res.length; i++) {
          if (res[i].bull > res[i].bear) _this.bull++;else _this.bear++;
        }
      });
    }
  }
};
